$(document).ready(function(){$window=$(window);$("[data-type]").each(function(){$(this).data("offsetY",parseInt($(this).attr("data-offsetY")));$(this).data("Xposition",$(this).attr("data-Xposition"));$(this).data("speed",$(this).attr("data-speed"))});$('div[data-type="background"]').each(function(){var a=$(this),d=a.offset().top;$(window).scroll(function(){if($window.scrollTop()+$window.height()>d&&d+a.height()>$window.scrollTop()){var c=-($window.scrollTop()/a.data("speed"));a.data("offsetY")&&(c+=
a.data("offsetY"));a.css({backgroundPosition:"50% "+c+"px"});$('[data-type="sprite"]',a).each(function(){var a=$(this),b=-($window.scrollTop()/a.data("speed")),b=a.data("Xposition")+" "+(b+a.data("offsetY"))+"px";a.css({backgroundPosition:b})});$('[data-type="video"]',a).each(function(){var a=$(this),b=-($window.scrollTop()/a.data("speed"))+a.data("offsetY")+"px";a.css({top:b})})}})})});
